import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { LoginUser, reset } from "../features/authSlice";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, isError, isSuccess, isLoading, message } = useSelector(
    (state) => state.auth
  );

  useEffect(() => {
    if (user || isSuccess) {
      navigate("/dashboard");
    }
    dispatch(reset());
  }, [user, isSuccess, dispatch, navigate]);

  const Auth = (e) => {
    e.preventDefault();
    dispatch(LoginUser({ email, password }));
  };

  const imageStyle = {
    height: "100vh",
    backgroundImage:
    'url(https://images.pexels.com/photos/16756606/pexels-photo-16756606/free-photo-of-pegunungan-pria-pasangan-wanita.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2)',
    // backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
 };
  

  return (
    <section className="hero is-fullheight is-fullwidth ">
      <div className="hero-body">
        <div className="container ">
          <div className="columns is-centered ">
            <div className="column is-4">
              <form onSubmit={Auth} className="box has-background-white $box-shadow">
                {isError && <p className="has-text-centered ">{message}</p>}
                <h1 className="title is-2 has-text-centered ">SIGN IN</h1>
                <div className="field">
                  <label className="label ">Email</label>
                  <div className="control">
                    <input
                      type="text"
                      className="input"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Email"
                    />
                  </div>
                </div>
                <div className="field">
                  <label className="label ">Password</label>
                  <div className="control">
                    <input
                      type="password"
                      className="input"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      placeholder="******"
                    />
                  </div>
                </div>
                <div className="field mt-5">
                  <button
                    type="submit"
                    className="button is-success is-fullwidth"
                  >
                    {isLoading ? "Loading..." : "Login"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
