import React from "react";
import { useSelector } from "react-redux";
import img from "../logo/bg-1.jpg";


const Welcome = () => {
  const { user } = useSelector((state) => state.auth);

  const imageStyle = {
    height: "100vh",
    backgroundImage:
    'url("https://images.pexels.com/photos/12198537/pexels-photo-12198537.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2")',
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
 };
  
  return (
    <div style={imageStyle}>
      <h1 className="title has-text-white">Dashboard</h1>
      <h2 className="subtitle has-text-white">
        Welcome Back <strong sty>{user && user.name}</strong>
      </h2>
    </div>
  );
};

export default Welcome;
